import base64
import datetime

from pydantic import BaseModel

FAKE_NAME = "RESCAN_API.pdf"
FAKE_PE = base64.b64decode(
    rb""  # noqa
)
FAKE_PDF = base64.b64decode(
    rb""  # noqa
)


class Settings(BaseModel):
    SANDBOX_HOST: str = "10.120.3.123"
    UI_TOKEN_LIFETIME: datetime.timedelta = datetime.timedelta(minutes=8)
    DEBUG: bool = False


settings = Settings()


def create_debug_json(data: bytes, report_name: str = "report_debug.json") -> None:
    if settings.DEBUG:
        with open(report_name, "wb") as f:
            f.write(data)  # debug
